void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / iResolution.xy;
    float time = iTime;
	vec3 c=vec3(0.,0.,0.);
    for(int i=1;i<160;i++){
        float j = float(i);
        float t = 2.*3.14*j/160. * sin(radians(time*0.001*j));
        float x = cos(j-t*j)+2.;
        float y = sin(j-t/j)+2.;
        vec2 o = .25*vec2(x,y)+(sin(j)*.1);
        c += 0.01/length(p-o)*vec3(0.1,0.1,0.1);
    }
    fragColor = vec4(c,1.);
}
