#define RADIUS1 .09
#define RADIUS2 .018
#define AMPLI .04
#define BLUE vec4(.11, .15, .2, 1.)
#define WHITE vec4(1., .98, .9, 1.)
#define RED vec4(.6, .09, .08, 1.)

vec4 aalias(float radius, float dist, vec4 bg, vec4 color, float ratio){
    return mix(bg, color, smoothstep(radius, radius * ratio, dist));
}

void mainImage( out vec4 pixColor, in vec2 pixCoord ) {
    vec2 pos = (pixCoord.xy / 600.) - vec2(.2, .3);

    if (pos.x < 0.) pixColor = RED;
    else if (pos.y < .19 && pos.x < RADIUS1 + AMPLI) {

    	float timeLoop = sin(iTime / 2.) / 10.;

        vec2 circle = vec2(abs(timeLoop) < AMPLI ? timeLoop : AMPLI * (abs(timeLoop) / timeLoop), 0.);
        vec2 circle1 = vec2(circle.x + .03, RADIUS1 / 2.);

    	vec2 delta = pos - circle1;

	    if(abs(circle1.x - .07) < AMPLI * .1) delta.x /= .05 + abs(cos(timeLoop * 100.));
	    float dist = distance(pos, circle);
        float dist2 = abs(sin(pos.y * 100.) / 80.);
	    if (pos.x < dist2 && abs(pos.y) > .095 && abs(pos.y) < .19) pixColor = aalias(dist2, pos.x, BLUE, WHITE, .7);
        else {
            float dist3 = length(delta);
            dist2 = length(vec2(delta.x, pos.y + circle1.y));
         	if(dist2 < RADIUS2 || dist3 < RADIUS2)
	    		pixColor = aalias(RADIUS2, dist2 < RADIUS2 ? dist2 : dist3, WHITE, BLUE, .8);
            else if (distance(pos, circle) < RADIUS1) 
                pixColor = aalias(RADIUS1, dist, BLUE, WHITE, .95);
            else pixColor = BLUE;
        }
    }
    else pixColor = BLUE;
}
