void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec3 color = vec3(0.0, 0.0, 0.0);
	vec3 colorSpheres = vec3(1.0, 1.0, 1.0);
	vec3 q = vec3(fragCoord.xy / iResolution.y, 0) - vec3(0.8, 0.4, 0);
	float rSphere = 0.03;
	float r = 0.2 * cos(iTime);
    float PI = 3.14159265359;

	for(int k = 0; k < 7; k++){
		for(int i = 0; i < 30; i++) {
			float theta = PI / 7.0 * float(k);
			float phi = float(i) * 2.0 * PI / 30.0;
			vec3 pos = r * vec3(sin(theta) * cos(phi), sin(theta) * sin(phi), cos(theta));
			colorSpheres *= smoothstep(rSphere, rSphere + 0.0001, length(q - pos));
		}
	}
	fragColor = vec4(color + vec3(1, 1, 1) - colorSpheres, 1);
    //fragColor = vec4(q.xyz, 1.0);
}
