#define radius 20.0

float getSpotlight(vec2 uv, float timex, float timey)
{
    vec2 spotlightLocation = iResolution.xy*vec2(0.5+0.4*sin(iTime*timex/10.0), 0.5+0.4*sin(iTime*timey/10.0));
    float dx = spotlightLocation.x-uv.x;
    float dy = spotlightLocation.y-uv.y;
    float dist = sqrt(dx*dx+dy*dy)/radius;
    return max(0.0, 1.0-dist*dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    float amt = 0.0;
    for(int i = 0; i < 100; i++)
        fragColor += getSpotlight(uv.xy, amt+=0.3, amt+amt*0.3);
    fragColor.r += sin(iTime/1.0);
    fragColor.g += sin(iTime/2.0);
    fragColor.b += sin(iTime/3.0);
    fragColor *= abs(sin(iTime))*0.4+0.1;
}
