void mainImage( out vec4 col, in vec2 p ) {
	p /= iResolution.xy;

    p.y = 1. - p.y;

    float r = 0.0;
    float pass = 0.0;

    for (int i = 0; i < 50; i++) {
        r += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x + cos(pass)*0.01, p.y + sin(pass)*0.05)).r);
        pass += 1.0;
    }
    r /= pass;

    float g = 0.0;
    pass = 0.0;

    for (int i = 0; i < 50; i++) {
        g += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x + cos(pass)*0.01, p.y + sin(pass)*0.05)).g);
        pass += 1.0;
    }
    g /= pass;

    float b = 0.0;
    pass = 0.0;

    for (int i = 0; i < 50; i++) {
        b += smoothstep(0.3, 0.4, texture(iChannel0, vec2(p.x + cos(pass)*0.01, p.y + sin(pass)*0.05)).b);
        pass += 1.0;
    }
    b /= pass; 

    col.rgb = vec3(0.0) + vec3(r, g, b);
}
