void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    //warping effect
    uv -= .5;
    uv *= pow(length(uv*2.),-.4);
    uv += .5;

    //chromatic abberarationg
    //not sure if this works on all devices
    for(int i = 0; i < 3; i++){
		fragColor[i] = texture(iChannel0, uv + 3.*float(i)/iResolution.xy)[i];
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy-.5;
    uv /=iResolution.x/iResolution.y;

    //rotation
    float ta = iTime/10., s = sin(ta), c = cos(ta);
    uv*=mat2(c,s,-s,c);

    //FOV
    uv *= 4000.;

    vec3 col = vec3(0);
    for(float i = 0.; i < 125.; i++)
    {
		vec3 pos = 700.*cos(i*vec3(21.32,423.123,213.23))-30.; //star's coord

        pos.z = mod(pos.z-30.*iTime,45.); //move stars

        vec2 screenpos = 50.*pos.xy/pos.z; //convert to 2d screen coordinates
        col += 8.*(1.-pos.z/50.)/(1.+length(uv-screenpos))*(.5*sin(i*vec3(7.12,9.42,4.11)+iTime)+.5); //color dots
    }

    //blend with last frame
    col += clamp(texture(iChannel0, fragCoord/iResolution.xy).xyz-.5,-.14,1.);

    col = clamp(col,0.,1.);

	fragColor = vec4(col,1);
}
