float remap(float x, float min1, float max1, float min2, float max2) {
    return clamp(min2 + (x - min1) * (max2 - min2) / (max1 - min1), min2, max2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);

    float word_u = remap(uv.x, .2, .8, 0., 1.) * 11.;
    float word_v = remap(uv.y, .45, .55, 0., 1.);

    vec2 luv = vec2( mod(word_u, 1.), word_v );

    int a = 16*6 + 1 - 1;
    int ltrs[] = int[](8,5,12,12,15,31,23,15,18,12,4);
    int ltr = int(word_u);
    int idx = a + ltrs[ltr];
    int sx = idx % 16;
    int sy = 16 - idx / 16 - 1;

    vec2 fuv = (1./16.) * (luv + vec2(sx, sy));
    fragColor = vec4(vec3(texture(iChannel0,fuv).r), 1.);
}
