#define S(v, r) smoothstep(-blur, blur, v-(r))

float circle(vec2 center, float radius, float blur)
{
    return S(radius, length(center));
}

float box(vec2 center, float width, float height, float blur)
{
    return  ( S(   width, center.x)
             -S(  -width, center.x) 
             )
        	*
        	( S( height , center.y)
             -S( -height, center.y) 
             );

}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.x;

    float m = sin(uv.x*7.+iTime)/25.;
    float n = sin(uv.y*7.+iTime*.75)/100.;
    uv.y += m;
    uv.x += n;

    vec3 color = vec3(box(uv, .5, .3, 2./iResolution.x));

    color.gb -= vec2(circle(uv, 0.2, 2./iResolution.x));

    fragColor= vec4(color, 1.);


}
