#define PI 3.1415926
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 s = texture( iChannel0, uv );
    vec4 s2 = texture( iChannel1, uv );

	float a = iTime * PI / 2.0;	//	angle in radians

    vec4 dy = sin( a ) 	* dFdy(s) * 10.0;	//	dx
    vec4 dx = cos( a ) 	* dFdx(s) * 10.0;	//	dy
	fragColor = dx + dy + s2;
}
