#define AA_SIZE 4.0
#define pi 3.141592653589793

float star(vec2 uv) {
	float a = atan(uv.y,uv.x);
    float r = length(uv);
    float starR = 0.5+0.25*pow(sin(a*2.5+iTime), 2.0);
    return r < starR ? 1.0 : 0.0;
}

float pointyStar(vec2 uv) {
	float a = atan(uv.y,uv.x);
    float r = length(uv);
    float starR = 0.5+0.25*(abs(mod(a*2.5+iTime, pi)/(0.5*pi)-1.0));
    return r < starR ? 1.0 : 0.3+pow(r-starR,0.8);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    for (float x=0.0; x < AA_SIZE; x++) {
        for (float y=0.0; y < AA_SIZE; y++) {
            vec2 aspect = vec2(iResolution.x/iResolution.y, 1.0);
            vec2 uv = (fragCoord.xy + vec2(x,y)/AA_SIZE) / iResolution.xy;
            uv = (2.0 * uv - 1.0) * aspect;
            fragColor += pointyStar(uv)*vec4(1.0-abs(uv),0.5+0.5*sin(iTime),1.0);
 		}
    }
    fragColor /= AA_SIZE * AA_SIZE;
}
