vec2 tr(vec2 p, vec2 res)
{
	p = -1.0+2.0*p/res;
    p.x *= res.x/res.y;
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = tr(fragCoord.xy - iMouse.xy + iResolution.xy/2.0,iResolution.xy);

    vec2 mouse = tr(iMouse.xy,iResolution.xy);

    vec3 c1 = vec3(1.0,0.5,0.0);
    vec3 c2 = vec3(0.2,0.7,0.5);

    float angle = degrees(atan(p.y,p.x));
    float len = length(p)*10.0;
    vec2 off = mouse;
    float t = smoothstep(0.,0.5,fract(iTime));

	float m = angle + (off.x/5.0)*log(len)*200.0 + t*60.0;
    float m2 = m-2.0*(off.x/5.0)*log(len)*200.0;

	float val = mix(mod(m,60.0),mod(m2,60.0),1.0/len);
	val = smoothstep(len,len*0.5,val*0.1)*exp(-2.0/len);


    vec3 col = mix(c1,c2,val);
    col = mix(col,col.rrb,off.x);
    col = mix(col,col.grg,off.y);

	fragColor = vec4(col,1.0);
}
