float sine(float value)
{
    return (sin(value) + 1.0) / 2.0;
}

const int amount = 8;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 mix = vec3(0.0, 0.0, 0.0);

    for (int i = 0; i < amount; i++)
    {
        float lx = sin(iTime * 1.43 + float(i) * 5.43) * 665.0;
        float ly = sin(iTime * 1.76 + float(i) * 7.34) * 454.0;
        float lgx = iResolution.x / 2.0 - fragCoord.x - lx;
        float lgy = iResolution.y / 2.0 - fragCoord.y - ly;
        float mul = (1.0 - (lgx * lgx + lgy * lgy) / (100000.0 + sin(iTime * 0.35 + float(i) * 324.0) * 40000.0));
        if (mul > 0.0) mix += vec3(sine(float(i) * 2.43 + 0.42), sine(float(i) * 4.24 + 1.54), sine(float(i) * 5.82 + 1.43)) * mul;
    }

    fragColor = vec4(mix, 1.0);
}
