renderpass.code:
#define T0(U,dx,dy) texture(iChannel0, U + vec2(dx,dy)/iChannelResolution[0].xy )
#define T1(U,dx,dy) texture(iChannel1, U + vec2(dx,dy)/iChannelResolution[1].xy )
#define T2(U,dx,dy) texture(iChannel2, U + vec2(dx,dy)/iChannelResolution[2].xy )
#define L(U,dx,dy) length( T1(U,dx,dy).rgb )

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;

    vec2 D = vec2 ( L(U,1,0)-L(U,-1,0),             // gradient
                    L(U,0,1)-L(U,0,-1) ) / 2.;
/*  for (int k = -1; k<=1; k++)
        D += vec2 ( L(U,1,k)-L(U,-1,k), 
                    L(U,k,1)-L(U,k,-1) ) / 6.;  */
    D *= iChannelResolution[0].y;

    int t = int(iTime/4.)%3;
    switch(t) {                                    // choose displacement
        case 0: D *= -.1; break;                   //   erosion
        case 1: D *=  .2; break;                   //   dilatation
        case 2: D = .2 * vec2(-D.y,D.x); break;    //   stretch
    }

    O -= O;
    float l, n=0.;
  //l=1.;  
  //for ( l = -1.; l<1.; l+=1./10.)                // LIC centered
    for ( l = -0.; l<1.; l+=1./10.)                // LIC lateral
  //for ( l = .5; l<1.5; l+=1./10.)                // LIC decentered
        O += T0(U,l*D.x+0.,l*D.y), n++;            // +0: add a displacement if you wish

    O = mix( O/n, T1(U,0,0), .1 );                 // relaxation
}
