void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy *200.;
    float w = .15+cos(iTime*.5)*.14;
	float m = step(pow((sin(uv.x+uv.y*.1- iTime*20.8)+1.)*.5,2.2),w);
	float n = step(pow((sin(uv.x+uv.y*.2+ iTime*20.2)+1.)*.5,2.2),w);
	float o = step(pow((sin(uv.x+uv.y*.4+ iTime*2.6)+1.)*.5,2.2),w);

	fragColor = vec4(m,n,o,1.0);
}
