float s( vec2 a ) { a = sin( a ); return a.x + a.y; }

void mainImage( out vec4 o, vec2 i ) {
	i /= iResolution.xy;
    for ( int j = 0 ; j < 3 ; j++ )
    	o[j] = s( 16. * i ) * s( 9. * i + iDate.w ),
        i *= vec2(-2,2);
   o = log( o + 1. );						//	comment these 
   o *= .01 / max( o.x, max( o.y, o.z ) );	//	2 lines is interesting too -- 158 chars
}
