renderpass.code:
vec4 lines(vec2 p,float x, float y,float n,float t)
{

    vec4 c = vec4(0.0,0.0,0.0,1.0);

    c.x = (sin(x*36.0 + t) / 2.0 + 0.5);
    c.y = (sin(y*18.0 + t*2.0) / 2.0 + 0.5);
    c.z = 1.0;

	p += vec2(0.0,iTime/40.0);

    c.x = step(texture(iChannel0,p).x/2.0 + n,c.x);
    c.y = step(texture(iChannel0,p).y/2.0 + n,c.x);
    c.z = step(texture(iChannel0,p).x/2.0 + n,c.y);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 p = fragCoord.xy / iResolution.xy;



	fragColor = lines(p,-p.x,-p.y,0.5,iTime * 0.0);
    fragColor += lines(p,-p.y,p.x,0.5,iTime * 0.0);

    fragColor = clamp(fragColor,0.0,1.0);


    fragColor.x *= 0.9;
    fragColor.y *= 0.9;
    fragColor.z *= 0.0;


    fragColor = clamp(fragColor,0.0,1.0);

    if(fragColor.x < 0.1)
    {
    	fragColor.z = 0.9;
    }

}
