void drawPoint(vec2 uv, vec2 c, float radius, vec4 color, inout vec4 fragColor)
{
    c.x *= iResolution.x / iResolution.y;

    float a = 1.0 - smoothstep(0.0, 0.005, distance(uv, c) - radius);

    a = color.a * a;

    fragColor = mix(fragColor, color, a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv.x *= iResolution.x / iResolution.y;

    fragColor = vec4(0,0,0,1);

    drawPoint(uv, vec2(0.2, 0.4), 0.01, vec4(1.0, 1.0, 1.0, 0.4), fragColor);    
    drawPoint(uv, vec2(0.7, 0.3), 0.05, vec4(1.0, 1.0, 1.0, 1.0), fragColor);
    drawPoint(uv, vec2(0.4, 0.4), 0.001, vec4(1.0, 1.0, 1.0, 1.0), fragColor);
    drawPoint(uv, vec2(0.5, 0.1), 0.1, vec4(1.0, 1.0, 1.0, 0.5), fragColor);
    drawPoint(uv, vec2(0.3, 0.7), 0.04, vec4(1.0, 1.0, 1.0, 0.8), fragColor);
    drawPoint(uv, vec2(0.9, 0.9), 0.02, vec4(1.0, 1.0, 1.0, 1.0), fragColor);
}
