// Load custom texture by executing this line in your browser's console:
// gShaderToy.SetTexture(0, {mSrc:'https://dl.dropboxusercontent.com/s/vqhyz58xzh9rbxx/Untitled-1%20copy.jpg?dl=0', mType:'texture', mID:1, mSampler:{ filter: 'nearest', wrap: 'clamp', vflip:'true', srgb:'false', internal:'byte' }});
//
// Loading custom texture method thanks to AntoineC in https://www.shadertoy.com/view/lsGGDd

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    float shift = 0.015;

	vec2 uv = fragCoord.xy / iResolution.y;
    float r = texture (iChannel0, uv + vec2 (-shift * uv.x, shift * uv.y), 1.0).r;
    float g = texture (iChannel0, uv                                     , 1.0).g;
    float b = texture (iChannel0, uv + vec2 (shift * uv.x, -shift * uv.y), 1.0).b;

	fragColor = vec4(r, g, b, 1.0);
}
