void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) + vec2(0., 0.1* iDate.w);

    //draw a 1 or a 0 (a square)
    vec2 m = vec2(.04,.02) * iResolution.xy;
    vec2 squv = fract(uv * m);
    float s = 5.;
    float sq = floor(s*max(abs(squv.x- .5), abs(squv.y - .5)));     


    //sample the noise texture to have a 0 or 1 map
    vec4 c = texture(iChannel0, floor(uv * m)/m);
    c = floor(c+.5);

    //invert the square depending on the data
    sq = mix(sq, 1.- sq, c.r);

	fragColor = vec4(sq);
}
