void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

   float bang = step (0.5, uv.x);


    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    float d = length(uv);
     float c = d;
    vec4 color_bg = vec4(1.0,0.0,0.5,1.0);
    vec4 color_circle = vec4(0.0,0.5,0.0,1.0);






    if (d< 0.35) c = 1.0; else c < 0.0;

    color_circle = mix( color_bg, color_circle, c);



    fragColor = vec4(vec3(c), 1.0) + (bang * color_bg);

    //fragColor = vec4(vec3(color_circle),1.0) - (bang * color_bg);
    fragColor = vec4(vec3(color_circle),1.0) * (((bang * color_bg) + (1.0 - c)) * sin(iTime) * 2.0);
}
