void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //how many squares in total
    vec2 scale = vec2(21.0, 13.0);

    //how many repeations
    vec2 repeat = vec2(3.0, 2.0);

    vec2 index = floor(scale * uv)/scale;
    index = fract(index*repeat);

    vec3 col = vec3(index, 0.);

	fragColor = vec4(col,1.0);
}
