const float sqrt2 = inversesqrt(0.5);

float dHyperbola(vec2 v){
    float xy = inversesqrt(v.y/v.x);
    vec2 hPoint = vec2(xy, 1.0/xy);
    return distance(v, hPoint);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord - iResolution.xy)/iResolution.y;
    float beta = iMouse.xy==vec2(0)?0.0:(2.0*iMouse.x/iResolution.x - 1.0);
    float rapidity = atanh(beta);
    vec2 v2 = vec2(cosh(rapidity), sinh(rapidity));
    mat2 transform = mat2(v2.x, v2.y, v2.y, v2.x);
    vec2 tp = p*transform;
    tp *= 10.0;
    ivec2 ip = ivec2(floor(tp));
    fragColor = vec4((ip.x + ip.y) % 2 == 0);
    p *= mat2(sqrt2, -sqrt2, sqrt2, sqrt2);
    if(p.x < 0.0 && p.y < 0.0) p = -p;
    if(dHyperbola(p) < .05) fragColor = vec4(1, 0, 0, 1);
}
