void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord + fragCoord - iResolution.xy ) / iResolution.y;
	vec2 center = vec2(0.0, 0.0);
	vec3 tmpColor = vec3(255, 255, 204);

	if (length(uv - center) < 0.2)
		tmpColor = vec3(0, 104, 55);
	else if (length(uv - center) < 0.4)
		tmpColor = vec3(49, 163, 84);
	else if (length(uv - center) < 0.6)
		tmpColor = vec3(120, 198, 121);
	else if (length(uv - center) < 0.8)
		tmpColor = vec3(173, 221, 142);
	else if (length(uv - center) < 1.0)
		tmpColor = vec3(217, 240, 163);

	tmpColor /= 255.0;
	fragColor = vec4(uv, 0.5 + 0.5 * 1.0, 1.0);
	fragColor = vec4(tmpColor, 1.0); 
}
