void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.xy;


    if(uv.x>0.5){
	fragColor = vec4(sin((iTime*1.)+uv.x) +uv.y,
                     cos((iTime*5.)-uv.y)+uv.x,
                     cos(uv.x+iTime),1.);
    }else{

	fragColor = vec4(cos((iTime*1.)+uv.x) +uv.y,
                     sin((iTime*5.)-uv.y)+uv.x,
                     cos(uv.x+iTime),1.);    
    }
}
