// fixed & optimized by iq & coyote
// also squared i so it's a bit less chaotic. i don't know, i like it better this way.
void mainImage(out vec4 o, vec2 i)
{
    o.xyz=iResolution;
    o=fract(vec4(o.a=dot(i=i/o.xy-.5,i)+atan(i.x*=o.x/o.y,i.y)*.955-iDate.w,o.a+i*i,1));
}
