void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = (iTime*0.1);

    vec2 p = fragCoord.xy / iResolution.xy;

    vec3 T = texture( iChannel1, p ).xyz;
    vec3 D = texture( iChannel1, p+0.005 ).xyz;
    float N = texture( iChannel0, vec2(p.x*2.0+time*0.2, p.y*2.0+time*0.2) ).x;
    float Z = texture( iChannel2, vec2(p.x*5.5, p.y*5.5+time*0.1) ).x;


	float col = smoothstep( 0.0, 0.5, N*Z );


    float N2 = texture( iChannel0, vec2(p.x*2.0, p.y*0.01+time) ).x;
    float Z2 = texture( iChannel0, vec2(p.x*3.0+time*0.1, p.y*0.03+time) ).x;



    float precip = 0.5;
    float pbrt = 1.0-abs(p.y-0.5);
	float sn = smoothstep( 1.0-precip, 1.0, Z2 )*0.1*pbrt + smoothstep( 1.0-precip, 1.0, N2 )*0.1*pbrt*pbrt*pbrt;



    fragColor = vec4( T.x*col + D.x*(1.0-col)*0.95 + sn, T.y*col + D.y*(1.0-col)*0.95 + sn, T.z*col + D.z*(1.0-col)*0.99 + sn, 1.0 );
}
