// Hash44 with floatBitsToInt and intBitsToFloat by Benjamin "BeRo" Rosseaux, public domain

// Loosely inspired from the ideas of http://trone.di.fc.ul.pt/images/6/61/PPAM11-paper.pdf but here accordingly 
// without expensive initialization with 20 rounds, so that it is here completely based on on-the-fly computations.

// I'm not using floatBitsToUInt and uintBitsToFloat here, since it seems that it is missing at some drivers, therefore,
// I'm using floatBitsToInt and intBitsToFloat together with ivec4 <=> uvec4 casting

vec4 hash44(vec4 p){

  uvec4 v = uvec4(floatBitsToInt(p));

#if 1
  // Pre-inter-mixing of all components with all components with a single ChaCha20 cipher round primitive iteration
  v.x += v.y; v.w ^= v.x; v.w = (v.w << 16u) | (v.w >> 16u);
  v.z += v.w; v.y ^= v.z; v.y = (v.y << 12u) | (v.y >> 20u); 
  v.x += v.y; v.w ^= v.x; v.w = (v.w << 8u) | (v.w >> 24u);
  v.z += v.w; v.y ^= v.z; v.y = (v.y << 7u) | (v.y >> 25u); 
#endif

#if 1
  // Full avalanche integer (re-)hashing with as far as possible equal bit distribution probability
  // => http://burtleburtle.net/bob/hash/integer.html  
  v -= (v << 6u);
  v ^= (v >> 17u);
  v -= (v << 9u);
  v ^= (v << 4u);
  v -= (v << 3u);
  v ^= (v << 10u);
  v ^= (v >> 15u);
#endif

#if 1
  // Post-inter-mixing of all components with all components with a single ChaCha20 cipher round primitive iteration
  v.x += v.y; v.w ^= v.x; v.w = (v.w << 16u) | (v.w >> 16u);
  v.z += v.w; v.y ^= v.z; v.y = (v.y << 12u) | (v.y >> 20u); 
  v.x += v.y; v.w ^= v.x; v.w = (v.w << 8u) | (v.w >> 24u);
  v.z += v.w; v.y ^= v.z; v.y = (v.y << 7u) | (v.y >> 25u); 
#endif

  return vec4(intBitsToFloat(ivec4(uvec4(((v >> 9u) & uvec4(0x007fffffu)) | uvec4(0x3f800000u))))) - vec4(1.0);

}      

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	fragColor = hash44(vec4(fragCoord.xy / iResolution.xy, iTime, 0.0));
}
