//Circle positons
vec2 circlePos1 = vec2(0.2,0.5);
vec2 circlePos2 = vec2(0.4,0.5);
vec2 circlePos3 = vec2(0.6,0.5);
//Circle radius
float radius1 = 0.15;
float radius2 = 0.15;
float radius3 = 0.15;
//Circle colors
vec4 color1 = vec4(1.0,0.0,0.0,1.0);
vec4 color2 = vec4(0.0, 1.0, 0.0, 1.0);
vec4 color3 = vec4(0.0, 0.0, 1.0, 1.0);

vec4 BG = vec4(1.0,1.0,1.0,1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Fixes Aspect ratio
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;
    uv.y *= aspectRatio;

    //Converts y postion to updated ratios
    circlePos1.y *= aspectRatio; 
    circlePos2.y *= aspectRatio;
    circlePos3.y *= aspectRatio;

    //Cirlce 1
    if(length(circlePos1 - uv) < radius1)
    {
        fragColor = color1;
    }

    //Cirlce 2
    else if(length(circlePos2 - uv) < radius2)
    {
        fragColor = color2;
    }
    //Cirlce 3
    else if(length(circlePos3 - uv) < radius3)
    {
        fragColor = color3;
    }
    else
    {
        fragColor = BG;
    }
}
