float yTiles = 5.0;

vec4 tBlack = vec4(0.0, 0.0, 0.0, 1.0);
vec4 tWhite = vec4(1.0, 1.0, 1.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 texture0 = texture(iChannel0, uv);
    vec4 texture1 = texture(iChannel1, uv);


    int currentTileX = int(xTiles * uv.x);
    int currentTileY = int(yTiles * uv.y);

    if ((currentTileX + currentTileY) % 2 == 0)
    {
     	fragColor = tBlack;   
    }
    else
    {
        fragColor = tWhite;
    }


}
