void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   //Sets the number of rows and columns;
    float numRows = 7.0;
    float numColumns = 7.0;

    //Sets the UV
	vec2 uv = fragCoord.xy /iResolution.xy;
    float aspectRatio = iResolution.x/iResolution.y;

    uv.x *= aspectRatio;
    //Creates the black and white colors.
    vec4 blackColor = vec4(0.0, 0.0, 0.0, 0.0);
    vec4 whiteColor = vec4(1.0, 1.0, 1.0, 1.0);

    //rounds the functions to a full integer
 	uv.x = floor(numColumns * uv.x);
    uv.y = floor(numRows * uv.y);

    //Use the remainder to calulate the checkerboard
    //if they are equal, it sets to black, otherwise it sets to white
    if(mod(uv.x,2.0) == 0.0 && mod(uv.y, 2.0) == 0.0 || mod(uv.x,2.0) == 1.0 && mod(uv.y, 2.0) == 1.0)
    fragColor = blackColor;
    else
   	fragColor = whiteColor;


}
