float lightA1[8] = float[8](
    float(189),
    float(216),
    float(253),
    float(253),
    float(253),
    float(216),
    float(189), 
    float(21)    
);

vec4 lightColorA1(vec2 fragCoord)
{        
    ivec2 gridPos = ivec2(fragCoord) % 8;         

    float factor = lightA1[(gridPos.x + (7 - gridPos.y)) % 8]/255.;

    return vec4(vec3(factor), 1.);
}

const mat4 darkA1 = mat4(
    21,189,253,253,
    189,21,189,253,
    253,189,21,189,
    253,253,189,21
)/255.;

vec4 darkColorA1(vec2 fragCoord)
{        
    ivec2 gridPos = ivec2(fragCoord) % 4;       
    ivec2 mirrorPos = ivec2(fragCoord) % 8;   

    bvec2 mirror = lessThan(mirrorPos, ivec2(4));   
    int xCoords = (mirror.x)
        ? (7 - mirrorPos.x) % 4
        : gridPos.x;

    int yCoords = (mirror.y)
        ? (7 - mirrorPos.y) % 4
        : gridPos.y;    

	float factor = darkA1[xCoords][yCoords];  
    return vec4(vec3(factor), 1.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{     
   	vec2 uv = fragCoord.xy / iResolution.xy;

    if (uv.x < 0.5)
   		fragColor = lightColorA1(fragCoord);
    else 
        fragColor = darkColorA1(fragCoord);
}
