float map( vec3 p )
{
    return length(p) - 1.0;
}
float formPos(float s, vec2 canvas, float maxx)
{
    return s / (canvas.x / canvas.y) * maxx;
}
float formray(float m, float s, float x)
{
    return m * s / x * mod(length(s),m*x/fract(s/m));
}
float formHike(float hike)
{
    return fract(hike*sin(hike / hike));
}
float trace(vec3 shape, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i < 24; i++)
    {
        vec3 p = rd / shape;
        float d = map(p);
        t += d * 0.5;
    }
    return t;
}
vec3 formRelev(float s, vec2 canvas, float minimum, vec3 td, float time)
{
    float p = formPos(s, canvas, float(dot(td,td)));
    float x = p / formray(minimum, smoothstep(0.0,1.0,float(sin(td*vec3(p)))),0.1);
    float vx = x / p * (canvas.x / canvas.y) + max(x,minimum);
    float rx = vx * x / p * sin(time);
    float mixed = p / x * (rx - mod(x, vx) * minimum);
    vec3 arc = vec3(p / fract(cos(formHike(1.1 * 2.0 * sin(float(iTimeDelta)+iTime)/(canvas.x/canvas.y)*2.0))));
    return vec3(arc[1] * 2.0, arc[0] / length(s), arc[2] + sin(time));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 shape = formRelev(1.0, uv, 3.1, normalize(vec3(uv,1.0)), iTime / length(fragCoord.x));
    float t = trace(shape, normalize(vec3(uv, 1.0)));
    float fog = 1.0 / (1.0 * t * t * 0.1);
    fragColor = vec4(vec3(fog),1.0);
}
