precision mediump float;

vec3 BACKGROUND(vec2 XY);
vec3 V_L_SPECTRUM(float X, float ANGLE);
float BELL_CURVE(float X1, float Q, float OFFSET_X);


float BELL_CURVE(float X1, float Q, float OFFSET_X)
{
    return pow(2.71828f,(Q * (-3.1415927f * pow((X1 - OFFSET_X),float(2)))));
}

vec3 V_L_SPECTRUM(float X, float ANGLE)
{
    float X0 = (pow((0.76f * X),float(2)) + (0.3f + ANGLE));
    return vec3(
        ((0.9f * BELL_CURVE(X0,0.8f,3.1f)) + (0.25f * BELL_CURVE(X0,1.0f,0.8f)) + (- (0.1f * BELL_CURVE(X0,1.0f,1.9f))) + 0.1f),
        ((1.0f * BELL_CURVE(X0,0.5f,2.2f)) + 0.1f),
        ((0.7f * BELL_CURVE(X0,1.0f,1.6f)) + 0.1f));
}

vec3 BACKGROUND(vec2 XY)
{
    return V_L_SPECTRUM((mod((float(2) * ((- XY.y) + (- (0.05f * pow(XY.x,float(2)))))),float(4)) - float(2)),(0.8f * sin((iTime * 0.2f))));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 OUTPUT0 = BACKGROUND((fragCoord.xy - vec2(iResolution.x/2.0f, 100.0f)) / iResolution.xy);


    fragColor = vec4(OUTPUT0, 1.0f);
}
