void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    float checkDist = 0.025 + abs(sin(((iTime*floor(uv.x*10.0))+atan(mod(uv.x, 0.1)-0.05, mod(uv.y, 0.1)-0.05)*floor(uv.y * 10.0)))) * 0.025;
    float val = (distance(mod(vec2(uv.x, uv.y), 0.1), vec2(0.05, 0.05)) < checkDist) ? 1.0 : 0.0;
    fragColor = vec4(0.2, 0.3, 0.4, 1.0) * (1.0-val) + vec4(1.0, 0.9*uv.y, 0.4, 1.0) * val;
}
