void mainImage(out vec4 color, in vec2 pixCoords) {

    color = vec4(0.4, 0.4, 1.0, 1.0);

    float SunX = iResolution.x /10.0;
    float SunY = iResolution.y /10.0;

    float distanceToSun = sqrt(pow(SunX - pixCoords.x, 2.0) + pow(SunY - pixCoords.y, 2.0));
    float rayon = iResolution.x/9.0;


    if (distanceToSun < rayon ) {
        color = vec4(1.0, 1.0, 0.0, 1.0);
        if (pixCoords.y <= iResolution.y/4.0) {
        color = vec4(0.0, 0.0, 1.0, 1.0);
    }
    }


    else if (pixCoords.y <= iResolution.y/4.0) {
        color = vec4(0.0, 0.0, 1.0, 1.0);
    }
}
