// (c>   Gigatron France 2016 for ST !
float ssh(float x) {	
	return  abs(fract(x))*40.0*0.15 ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 tx=texture(iChannel0,uv).xyz; 

    float t=23.+abs(sin(iTime*49.9));// 24.9*2. fps


    uv.y -= uv.y/40.+ssh(uv.x*t/300.);
    uv.x -= uv.x/40.+ssh(uv.y*(t/300.));


    // rgb component operations hit glasses ;
    vec3 col =(tx*t*0.02-tx.r)+(tx.g*1.0*t*0.08);

    // try to magmatization glasses  !

    col *=(tx*t*0.02-tx.r)+(tx.g*1.0*t*0.21)/(tx.b*5.00);

   // shadow ! ops! 
   //   vec3 col =(tx*t*0.02-tx.r)*(tx.g*1.0*3.88);



	fragColor = vec4(col,1.0);;
}
