// tracker added to a slowed version of https://www.shadertoy.com/view/XldXRN
// colored variant : https://www.shadertoy.com/view/MlcSz7

void mainImage( out vec4 O, vec2 U )
{	O = texture(iChannel0, U/iResolution.xy); }

#define T(x,y) texture(iChannel0, (U+vec2(x,y))/iResolution.rg)
vec4 red = vec4(1,0,0,1);

void mainImage( out vec4 O, vec2 U )
{
    O = vec4(1);
    if (   (iFrame<10 && U-.5==iResolution.xy/2.)
        || ( iMouse.z>0. && U-.5==iMouse.xy )     ) O = red;       // mark dot
    if (T(0,0)!=vec4(0))
        for (int j=-2; j<3; j++)
        	for (int i=-2; i<3; i++)
                if (T(i,j)==red) { O = red; break; }               // track dot

// ------ code similar to https://www.shadertoy.com/view/XldXRN ------------------------------
    vec2 R = iResolution.xy;
	U = ( U+U - R ) /R.y * 8.;  

    float Pi = 3.14159265359,
           t = 8.*iTime,
           e = 35./R.y, v;
  //       a = Pi/3.*floor(t/2./Pi);
  //U *= mat2(cos(a),-sin(a), sin(a), cos(a));              
    U *= mat2(sin(Pi/3.*floor(t/2./Pi) + Pi*vec4(.5,1,0,.5)));     // animation ( switch dir )

  	U.y /= .866; 
    U -= .5;   
    v = ceil(U.y);
    U.x += .5*v;                                                   // hexagonal tiling
  //U.x += sin(t) > 0. ? (.5-.5*cos(t)) * (2.*mod(v,2.)-1.) : 0.;  
	U.x +=  (1.-cos(t/2.)) * (mod(v,2.)-.5) ;                      // animation ( scissor )
  //U.x += (1.-cos(t/2.)) * (mod(v,2.)-.5);                        // variant

    U = 2.*fract(U)-1.;                                            // dots
    U.y *= .866;
	O *= smoothstep(e,-e, length(U)-.6);
}
