void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // RGBA
    vec4 color = vec4(1.0 ,0.5, 1.0, 0.5);
    color.r = 0.0;
    color.bg = vec2(0.0, 0.5);
    // pixel position
	vec2 pixel = fragCoord.xy / iResolution.xy;

    // center origin point 
    pixel = (pixel - 0.5) * 2.0;

    // aspect ratio
    pixel.x *= iResolution.x / iResolution.y;



    color.gr = pixel.xy;
    color.b = 1.0;

    // time elapsed
    float time = iTime;

    // oscillation
    float anim = sin(time) * 0.5 + 0.5;

    // circle
    float radius = 0.5 * anim * 0.5 +0.5;
    float circle = step(length(pixel), radius);
    //color.rgb *= circle;

    // smooth circle
    float smoothCircle = 1.0 - smoothstep(radius, radius + 0.2, length(pixel));
    //color.rgb *= smoothCircle;

    // rotation
    float angle = atan(pixel.y, pixel.x);
    float dist = length(pixel);
    angle += time;
	angle *= dist;
    pixel = vec2(cos(angle), sin(angle)) * dist;

    // square
    float square = step(abs(pixel.x), radius) * step(abs(pixel.y), radius);
    color.rgb *= square;

    // grid
    float lineSize = 0.02;
    float cellWidth = 0.2;
    float grid = step(cellWidth - lineSize, mod(pixel.x, cellWidth));
    grid += step(cellWidth - lineSize, mod(pixel.y, cellWidth));
    color.rgb *= grid;

	fragColor = color;
}
