void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;    

    float l = length(uv - 0.5);

    float ang = atan(uv.y - 0.5, uv.x - 0.5);
	ang = ang * 0.5 + 1.57;
    ang /= 3.14;

    vec2 ra = vec2(ang + l + iTime * 0.05);
    vec4 t = texture(iChannel0, ra + vec2(fract(iTime*0.01), 0.0) );
    fragColor = smoothstep(vec4(0.0), vec4(1.0), t);
}
