vec2 udc2uv( vec2 udc )
{
    return udc * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 udc = ( fragCoord / iResolution.xy ) * 2.0 - 1.0;

    float intensity = 0.03;
    vec2 uvr = udc2uv( udc * ( 1.0 + intensity ) );
    vec2 uvg = udc2uv( udc );
    vec2 uvb = udc2uv( udc * ( 1.0 - intensity ) );

    vec3 color = vec3( 
        texture( iChannel0, uvr ).r, 
        texture( iChannel0, uvg ).g, 
        texture( iChannel0, uvb ).b
        );

    fragColor = vec4( color, 1.0 );
}
