void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	//smoothstep图层
	vec4 layer1;
       float y = smoothstep(0.0,1.0,uv.x);
       float dis = length(y-uv.y);
       if(dis<0.005)
       	layer1 = vec4(1,0,0,1);
    else
        layer1 = vec4(0,0,0,0);
    // clamp图层
    vec4 layer2;
    float clampY = clamp(uv.x,0.0,1.0);
    float clampDis = length(clampY-uv.y);
    float t = 1.0 - step(0.005,clampDis);
    layer2 = vec4(0,t,0,t);


    fragColor = mix(layer1,layer2,layer2.a);  	   
    //fragColor = vec4(iMouse.y/100.0,0,0,1);//iMouse分量取值范围0-窗口尺寸，原点在左下角
}
