// variants: https://www.shadertoy.com/view/3sBGz1
//           https://www.shadertoy.com/view/WdB3zh

// --- load decode and display bit i / 4x32 from buffA

#define N(P,i) vec4( ( UVEC4(T(P))[i/32] >> (31-(i%32)) ) & 1U )

#define T(U)   texelFetch(iChannel0,ivec2(U),0)

void mainImage( out vec4 O, vec2 U )
{
    U = floor(U/4.); U.y -= 2.;
    int i = int(U.x);             // bit position
    O -= O; 
    if (i>=32*4) return;

    O.b = float(i%2==1) * .5;     // dislplay guides
    O.g = float(i%8==7) * .5;
    O.r = float(i%32==31);
    if (mod(U.y,10.)==0. && U.y<=50.) O.b = .5;


    if (U.y==50.) O += N(4,i);    // display bits 4x32
    if (U.y==40.) O += N(3,i); 
    if (U.y==30.) O += N(2,i);
    if (U.y==20.) O += N(1,i);
    if (U.y==10.) O += N(0,i);  

    uvec4 S = uvec4(0x80000000U,0,0,0); // bottom line:  directly, without storage
    if (U.y==0.) O += vec4( ( UVEC4(VEC4(S))[i/32] >> (31-(i%32)) ) & 1U );
}

// store 4x32bits in buffer RGBA

#define F   uintBitsToFloat(0xFFFF0000U)
#define S   uintBitsToFloat(0x80000000U)

void mainImage( out vec4 O, vec2 U )
{ 
    U -= .5;
    O = vec4(0);
    if (U==vec2(4)) O = vec4( F, 0, F, 0 );

    if (U==vec2(3)) O = vec4( F, S, 0, 0 );
    if (U==vec2(2)) O = vec4( 0, S, 0, 0 );
    if (U==vec2(1)) O = vec4( S, 0, F, 0 );
    if (U==vec2(0)) { O.x = S; O.z=0.; }
}
