void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float steps = 8.;
    float dither = 0.;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec4 color = texture(iChannel0, vec3(uv-vec2(0.5, 0.), 0.1)*vec3(1., -1., 1.));
    if(uv.x>mouse.x){
    	dither = texture(iChannel1, (uv-mouse)*1.5+iTime*0.001).r-0.5;
    } else {
        dither = 0.;
    }
    vec4 col = color+float(uv.x>mouse.x&&uv.y>mouse.y)*dither;
    if(uv.y>mouse.y||uv.x<mouse.x){
        col = floor(col*steps)/steps;
    }
    fragColor = col;
}
