const vec4 startTopLeft = vec4(0.0, 0.0, 0.7, 1.0);
const vec4 startTopRight = vec4(0.2, 0.4, 1.0, 1.0);
const vec4 startBottomLeft = vec4(0.2, 0.4, 1.0, 1.0);
const vec4 startBottomRight = vec4(0.0, 1.0, 0.0, 1.0);
const vec2 startBias = vec2(0.0, 0.0);

const vec4 finalTopLeft = vec4(0.4, 0.4, 1.0, 1.0);
const vec4 finalTopRight = vec4(1.0, 0.5, 0.0, 1.0);
const vec4 finalBottomLeft = vec4(0.0, 0.0, 0.7, 1.0);
const vec4 finalBottomRight = vec4(1.0, 0.0, 1.0, 1.0);
const vec2 finalBias = vec2(-0.2, 0.0);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = clamp (iMouse.x / iResolution.x, 0.0, 1.0);

    vec2 startUV = fragCoord / vec2(iResolution) + startBias;
    vec4 startColor = mix(mix(startBottomLeft, startTopLeft, startUV.y), mix(startBottomRight, startTopRight, startUV.y), startUV.x);

    vec2 finalUV = fragCoord / vec2(iResolution) + finalBias;
    vec4 finalColor = mix(mix(finalBottomLeft, finalTopLeft, finalUV.y), mix(finalBottomRight, finalTopRight, finalUV.y), finalUV.x);

    fragColor = mix(startColor, finalColor, t);
}
