#define EPSILON pow(10., -3.)
#define bigness 0.01
#define PI 3.1415926
vec3 tex (vec3 p, sampler2D t){
    vec3 t1 = texture(t, p.xy).rgb/3.;
    t1+=texture(t, p.xz).rgb/3.;
    t1+=texture(t, p.yz).rgb/3.;
    return t1;
}
float align (float diff, float dim){
    if(diff>=dim){
        return diff-dim;
    } else {
        return 0.;
    }
}
float box3D (vec3 boxPos, vec3 boxSize, vec3 pos){
    float n = align(abs(pos.x-boxPos.x), boxSize.x)+align(abs(pos.y-boxPos.y), boxSize.y);
    n+=align(abs(pos.z-boxPos.z), boxSize.z);//just to keep the lines short
    return n;
}
float sphere (vec3 spherePos, vec3 pos, float radius){
    return length(pos-spherePos)-radius;
}
//Credit to Inigo Quilez for original function-- https://iquilezles.org/articles/distfunctions
float CappedCylinder( vec3 pos, vec3 p, vec2 h )
{
  p = p-pos;
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float cylinder (vec3 pos, vec3 p, float radius, float height){
    vec3 diff = p-pos;
    float yd = abs(diff.y);
    return (length(diff.xz)-radius)+smoothstep(yd, -height*0.5, height*0.6);//+smoothstep(abs(diff.y)-height, 0., -height);
}
float doubleCone (vec3 pos, vec3 p, float radius, float height){
    vec3 diff = p-pos;
    float yd = abs(diff.y)-height;
    return (length(diff.xz)-radius)-yd;
}
//useful functions
vec2 rotate2D (vec2 p, float angle){
    float a = atan(p.y, p.x)+angle;
    float m = length(p);
    return vec2(cos(a)*m, sin(a)*m);
}
//from IQ -- https://iquilezles.org/articles/smin
float smin( float a, float b, float k)
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
float mymin (float a, float b, float k){
    return min(a, b)-abs(a-b)/(k*100.);
}
vec4 SDF (vec3 p){
    vec3 matCol = vec3(-1.);
    float total = 0.;

    float sc = 0.005;
    //modeling
    float mainBody = box3D(vec3(0., 0., 0.48)*sc, vec3(4., 0.5, 0.9)*sc, p);
    float trigger = box3D(vec3(3.5, 0.5, 0.28)*sc, vec3(0.5, 0.5, 1.1)*sc, p);

    float curveBody = box3D(vec3(0., 0., -0.9)*sc, vec3(3., 0.5, 0.5)*sc, p);
    float round1 = CappedCylinder(vec3(3.0, 0., -0.4)*sc, p, vec2(1., 0.5)*sc);
    float round2 = CappedCylinder(vec3(-3.0, 0., -0.4)*sc, p, vec2(1., 0.5)*sc);

    float homeButton = sphere(vec3(-2., -0.3+sin(iTime), 0.4)*sc, p, 0.5*sc);
    float otherButton = sphere(vec3(-2.+sin(iTime-0.01)*1.,0., 0.4)*sc, p, 0.8*sc);
    //distance field editing

    //green section
    total = smin(mainBody, trigger, 0.);
    total = min(curveBody, total);
    round1 = min(round1, trigger);
    total = min(total, round1);
    total = min(total, round2);
    total = min(total, trigger);
    if(total<0.0001&&matCol.x<0.){
        matCol = vec3(0.3, 1., 0.3);
    }
    //textured section
    total = max(total, -otherButton);
    if(total<0.0001&&matCol.x<0.){
        matCol = tex(p*20., iChannel1);
    }
    //black section
    total = smin(total, homeButton, 0.001);
    if(total<0.0001&&matCol.x<0.){
        matCol = vec3(0.);
    }
    return vec4(total, matCol);
}
vec4 trace (vec3 o, vec3 r) {
    float t = 0.;
    vec3 p = o;
    for(int i = 0; i<128; i++){
        p = o+r*t;
        float d = SDF(p).x;
        t+=d*0.6;
    }
    return vec4(p, t);
}
//Credit to Jamie Wong http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#surface-normals-and-lighting
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SDF(vec3(p.x + EPSILON, p.y, p.z)).x - SDF(vec3(p.x - EPSILON, p.y, p.z)).x,
        SDF(vec3(p.x, p.y + EPSILON, p.z)).x - SDF(vec3(p.x, p.y - EPSILON, p.z)).x,
        SDF(vec3(p.x, p.y, p.z  + EPSILON)).x - SDF(vec3(p.x, p.y, p.z - EPSILON)).x
    ));
}

//made this one myself with some tutorials I found out there on the web
float diffuseLight (vec3 p, vec3 n, vec3 r){
    return dot(n, -r);
}
float specularLight (vec3 p, vec3 n, vec3 r){
    vec3 nr = reflect(r, n);
    return pow(dot(nr, -r), 2.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //setup coordinates
    vec2 mouse = iMouse.xy/iResolution.xy - vec2(0.5, 0.);
    if(iMouse.xy == vec2(0.))mouse = vec2(0.5, 0.2);
    mouse.x*=5.;
    mouse.y*=2.;
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    //setup camera + rays
    float angle = mouse.x;
    float fov = 0.5;
    float camDist = bigness*6.;
    vec3 camera = vec3(cos(mouse.x)*camDist, 0., sin(mouse.x)*camDist);
    vec3 ray = vec3(-cos(mouse.x+uv.x*fov)*2., uv.y, -sin(mouse.x+uv.x*fov)*2.);
    ray = normalize(ray);
    mouse.y-=0.5;
    mouse.y*=-2.5;
    camera = vec3(rotate2D(camera.xy, mouse.y), camera.z);
    ray = vec3(rotate2D(ray.xy, mouse.y), ray.z);

    //do the raymarching
    vec4 v = trace(camera, ray);
    vec3 normal = estimateNormal(v.xyz);//keep here
    vec3 col = vec3(0.);
    //optional reflection:
    //col = texture(iChannel0, reflect(ray, normal)).rgb*0.5;
    col = SDF(v.xyz).yzw;//tex(v.xyz*5., iChannel1);

    //do the lighting https://learnopengl.com/Lighting/Basic-Lighting
    col+=diffuseLight(v.xyz, normal, ray)*0.2;
    col+=specularLight(v.xyz, normal, ray)*vec3(1., 1., 0.8)*0.3;//tint it yellow
    col *= 1./(1.+pow(v.w*2., 10.)*0.1);//distance shading

    // Output to screen
    fragColor = vec4(col,1.0);
}
