void mainImage( out vec4 O, vec2 uv )
{
    vec2 R = iResolution.xy,
         U = uv/R;
/*  uv = vec2(R.x/2.,0.) + vec2(uv.x-R.x/2.,R.y)/(1.-U.y);
    O = texture(iChannel0,uv/iChannelResolution[0].xy);
    O = .5 + (O-.5)*exp2(6.*U.y);
    return;
*/                  
    float l = (.5-.5*cos(U.x+.3*U.y+iTime))*6.;
    l = floor(l);
    O = texture(iChannel0,uv/iChannelResolution[0].xy, l);
  //O = O.xxxx;                                                        // for grey texture
    O = .5 + (O-.5)*exp2(l);                                           // normalized std-dev 

    O = sqrt(O);                                                       // gamma correction
}
