#define FUDGE_AMOUNT 0.06


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenCoord = fragCoord/iResolution.xy;
    vec2 fuckCoord = screenCoord;
    float fuckAmt = texture(iChannel3, vec2(cos(iTime*12.), sin(iTime*16.))).r;
    fuckAmt = pow(fuckAmt, 2.);
    fuckCoord.x += (sin(screenCoord.x*pow(iTime,2.7))*fuckAmt)/64.;
    fuckCoord.y += (cos(screenCoord.y*pow(iTime,2.3))*fuckAmt)/56.;

    //fuckCoord = mix(fuckCoord, ceil(fuckCoord), sin(iTime));

    vec4 co = texture( iChannel0, fuckCoord);
    vec4 oc = texture( iChannel1, screenCoord);
    vec4 test = mix(co, oc, co.g);
    if(co.g > 0.005 && ((co.g > co.r + FUDGE_AMOUNT) && (co.g > co.b + FUDGE_AMOUNT))){
        test = oc;
        //test = vec4(0., 0., 0., 1.);
    }
    else{
        vec4 noise = texture(iChannel3, screenCoord);
        noise.r = pow(noise.r, 2.);
        noise.g = pow(noise.g, 2.);
        noise.b = pow(noise.b, 2.);
        test = mix(co*oc*texture(iChannel2, vec2(screenCoord.x+(iTime/5.), screenCoord.y))*noise, oc, co.r);
    }
    fragColor = test;
}
