#define PI 3.14159265359
#define TWOPI 6.28318530718

void mainImage(out vec4 fragColor,in vec2 fragCoord){

   float time = iTime*0.5;
    float color1,color2,color;
    float mouseX_ratio=iMouse.x/iResolution.x;
    float mouseY_ratio=iMouse.y/iResolution.y;

    float A=0.015+mouseX_ratio*0.01;
    float B=0.02+mouseY_ratio*0.04 ;

  color1 = (sin(dot(fragCoord.xy,vec2(sin(time*3.0),cos(time*3.0)))*A+time*3.0)+1.0)/2.0;
                                                                 ///  |

	vec2 center = vec2(640.0/2.0, 360.0/2.0) + vec2(640.0/2.0*sin(-time*3.0),360.0/2.0*cos(-time*3.0));

	color2 = (cos(length(fragCoord.xy - center)*B)+1.0)/2.0;
                                              /// |
    color=(color1+color2)/2.0;

    float red	= (cos(color*PI*1.7+time*3.0)+1.0)/2.0;
	float green	= (sin(+time*3.0)+1.0)/2.0;
	float blue	= (sin(color*PI*1.7+time*3.0)+1.0)/2.0;

    fragColor = vec4(red,green,blue, 1.0);
}
