vec2 poltocar( vec2 pol ) {
    return vec2( pol.x * cos(pol.y), pol.x * sin(pol.y) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime;
    vec2 uv = fragCoord / iResolution.st;
    vec2 displacedUv = uv;
    vec4 imageIn = texture(iChannel1, uv);
    vec4 feedbackIn = texture(iChannel0, uv);

    // displacedUv += poltocar( vec2(length(feedbackIn.rg), length(feedbackIn.rb)) ) * 0.0005;
    displacedUv -= poltocar( vec2(length(feedbackIn.rg), length(feedbackIn.rb)) ) * 0.0003;
    vec4 feedbackOut = texture(iChannel0, displacedUv);

    feedbackOut.r = mod(feedbackOut.r + 0.001, 1.0);
    feedbackOut.g = mod(feedbackOut.g - 0.001, 1.0);
    feedbackOut.b = mod(feedbackOut.b + 0.001 * noise(time), 1.0);

    if(iFrame <= 10 || iMouse.z > 0.0){
        fragColor = imageIn;
    } else {
        fragColor = feedbackOut;
    }
}
