////////////////
// Wobbly sea //
////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float speed	  = 1.3;
    float heightA = 0.05;
    float heightB = 0.05;
    float heightC = 0.03;

    vec2 xy = fragCoord.xy;				    // We obtain our coordinates for the current pixel

    xy.x = xy.x / iResolution.x;		    // We divide the coordinates by the screen size
    xy.y = xy.y / iResolution.y;

    xy.y = sin((xy.x + (-iTime*speed))*5.0)*heightA + xy.y;
    xy.y = xy.y + sin((xy.x + (iTime))*3.0)*heightB;
    xy.y = xy.y + sin((xy.x + (iTime))*2.0)*heightC;     

    xy.y = xy.y * 4.0;

    vec4 finalColour = vec4(0,0.0,0.0,1.0);	// This is actually black right now

    finalColour.r = 0.2;					// Set its red component
    finalColour.g = 0.4;					// Set its green component
    finalColour.b = 0.8;					// Set its blue component

    if(xy.y < 0.9)

    {										// Arbitrary number, we don't know how big our screen is!
        finalColour.r = xy.y + 0.0;			// Set its red component to 1.0
        finalColour.g = xy.y + 0.4;			// Set its green component to 1.0
        finalColour.b = 1.0;				// Set its blue component to 1.0
    }

    fragColor = finalColour;
}
